package day1.basics;

public class CharExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'y';
		System.out.println(ch);
		
		char ch1 = 22;
		System.out.println(ch1);
		
		char var1 = '\u00E5';
		System.out.println(var1);
		
		char var2 = '\u30EE';
		System.out.println(var2);
		
		int a = 'A';
		System.out.println(a);

	}

}
